import { React, useState, useContext } from "react";
import { KorisnikContext } from "./KorisnikContext";
import axios from "axios";
const Prijava = () => {
  const [lozinka, setLozinka] = useState("");
  const handleLozinka = (event) => {
    setLozinka(event.target.value);
  };

  const [kime, setKime] = useState("");
  const handleKime = (event) => {
    setKime(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    const data = {
      kime,
      lozinka,
    };

    axios
      .get("http://localhost:5000/auth/", {
        params: { kime: "kime", lozinka: "lozinka" },
      })
      .then((res) => {
        setKorisnik(res.data);
        console.log("prijavljen");
      })
      .catch((err) => {
        console.log({ message: err.message });
      });
  };

  const { korisnik, setKorisnik } = useContext(KorisnikContext);
  return (
    <form onSubmit={handleSubmit}>
      <label>
        <p>Korisničko ime:{korisnik}</p>
        <input
          type="text"
          name="kime"
          placeholder="Korisničko ime"
          id="kime"
          value={kime}
          onChange={handleKime}
        />
      </label>

      <label>
        <p>Lozinka:</p>
        <input
          type="text"
          name="lozinka"
          placeholder="Lozinka"
          id="lozinka"
          value={lozinka}
          onChange={handleLozinka}
        />
      </label>
      <button type="submit">Prijava</button>
    </form>
  );
};

export default Prijava;
