import { React, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
const Provjera = () => {
  let { id } = useParams();
  const [data, setData] = useState();
  const [isLoading, setIsloading] = useState(true);
  useEffect(async () => {
    axios
      .get(`http://localhost:5000/kategorije/provjere/${id}`)
      .then((res) => {
        //let provjera = res.data;
        setData(res.data);
        setIsloading(false);
        //console.log("provjera", data[0].provjere);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);
  return (
    <div>
      <h1>Provjera</h1>
      <div>
        {!isLoading &&
          data[0].provjere
            .filter((p) => p._id.includes(id))
            .map((nez) => <p>{nez.naziv}</p>)}
      </div>
      <div>
        {!isLoading &&
          data[0].pitanja.map((p) => (
            <fieldset id={p._id}>
              <h2>{p.pitanje}</h2>
              {p.odgovori.map((o) => (
                <div>
                  <input type="radio" value={o} key={o} name={p._id} />
                  <label for={o}>{o}</label>
                </div>
              ))}
            </fieldset>
          ))}
      </div>
    </div>
  );
};

export default Provjera;
