import { React, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "./Provjera.css";
const Provjera = () => {
  let { id } = useParams();
  const [data, setData] = useState();
  const [isLoading, setIsloading] = useState(true);
  const [rjesenje, setRjesenje] = useState([]);
  const handleRjesenje = (event) => {
    rjesenje.length > 0
      ? rjesenje.map((item) =>
          item.pitanje_id === event.target.name
            ? setRjesenje([
                ...rjesenje,
                {
                  pitanje_id: "bilo je istih idova",
                  odgovor: event.target.value,
                },
              ])
            : setRjesenje([
                ...rjesenje,
                {
                  pitanje_id: "nije bilo istih idova",
                  odgovor: event.target.value,
                },
              ])
        )
      : setRjesenje([
          ...rjesenje,
          { pitanje_id: "bio je prazan array", odgovor: event.target.value },
        ]);
  };
  useEffect(async () => {
    axios
      .get(`http://localhost:5000/kategorije/provjere/${id}`)
      .then((res) => {
        //let provjera = res.data;
        setData(res.data);
        setIsloading(false);
        //console.log("provjera", data[0].provjere);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);
  return (
    <div>
      <div>
        {!isLoading &&
          data[0].provjere
            .filter((p) => p._id.includes(id))
            .map((nez) => <h1>{nez.naziv}</h1>)}
      </div>
      <div>
        {!isLoading &&
          data[0].pitanja.map((p) => (
            <fieldset id={p._id} className="field">
              <h4>{p.pitanje}</h4>
              {p.odgovori.map((o) => (
                <div>
                  <input
                    type="radio"
                    value={o}
                    key={o}
                    name={p._id}
                    onChange={handleRjesenje}
                  />
                  <label for={o}>{o}</label>
                </div>
              ))}
            </fieldset>
          ))}
      </div>
    </div>
  );
};

export default Provjera;
