import { React, useState, useContext } from "react";
import { KorisnikContext } from "../../dijeljeno/KorisnikContext";
import axios from "axios";

const KreirajPitanje = () => {
  const { user, setUser } = useContext(KorisnikContext);
  const [pitanje, setPitanje] = useState("");
  const handlePitanje = (event) => {
    event.preventDefault();
    setPitanje(event.target.value);
  };

  const [odgovor, setOdgovor] = useState("");
  const handleOdgovor = (event) => {
    setOdgovor([...odgovor, event.target.value]);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const data = {
      moderator: user._id,
      pitanje,
      odgovor,
    };

    axios
      .patch("http://localhost:5000/kategorije/dodaj_materijal", data)
      .then((res) => {
        setPitanje("");
        setOdgovor("");
      })
      .catch((err) => {
        console.log({ data });
        console.log({ message: err.message });
      });
  };
  console.log(user._id);
  return (
    <>
      <h1>Kreiraj pitanje</h1>
      <form onSubmit={handlePitanje}>
        <label>
          <p>Pitanje:</p>
          <input
            type="text"
            name="pitanje"
            placeholder="Pitanje"
            id="pitanje"
            value={pitanje}
          />
        </label>
        <button type="submit">Dodaj pitanje i odgovore</button>
      </form>
    </>
  );
};

export default KreirajPitanje;
