import React from "react";

const DodajPredmet = () => {
  const [predavac, setPredavac] = useState("");
  const handlePredavac = (event) => {
    setPredavac(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(naslov);
    setNaslov("");
    setTekst("");
  };
  return (
    <form onSubmit={handleSubmit}>
      <label>
        <p>Naslov:</p>
        <input
          type="text"
          name="naslov"
          placeholder="Naslov"
          id="naslov"
          value={naslov}
          onChange={handleNaslov}
        />
      </label>

      <label>
        <p>Tekst:</p>
        <textarea
          type="text"
          name="tekst"
          placeholder="Tekst"
          id="tekst"
          value={tekst}
          onChange={handleTekst}
        />
      </label>
      <button type="submit">Dodaj materijal</button>
    </form>
  );
};

export default DodajPredmet;
