import { React, useState, useContext } from "react";
import { KorisnikContext } from "./KorisnikContext";

const KreirajMaterijal = () => {
  const [naslov, setNaslov] = useState("");
  const handleNaslov = (event) => {
    setNaslov(event.target.value);
  };

  const [tekst, setTekst] = useState("");
  const handleTekst = (event) => {
    setTekst(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(naslov);
    setNaslov("");
    setTekst("");
  };
  const { user, setUser } = useContext(KorisnikContext);

  return (
    <form onSubmit={handleSubmit}>
      <label>
        <p>Naslov:</p>
        <input
          type="text"
          name="naslov"
          placeholder="Naslov"
          id="naslov"
          value={naslov}
          onChange={handleNaslov}
        />
      </label>

      <label>
        <p>Tekst:</p>
        <textarea
          type="text"
          name="tekst"
          placeholder="Tekst"
          id="tekst"
          value={tekst}
          onChange={handleTekst}
        />
      </label>
      <button type="submit">Dodaj materijal</button>
    </form>
  );
};

export default KreirajMaterijal;
