import { React, useState } from "react";

const DodajPredmet = () => {
  const [predavac, setPredavac] = useState("");
  const handlePredavac = (event) => {
    setPredavac(event.target.value);
  };

  const [predmet, setPredmet] = useState("");
  const handlePredmet = (event) => {
    setPredmet(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(predmet);
    setPredavac("");
    setPredmet("");
  };
  return (
    <form onSubmit={handleSubmit}>
      <label>
        <p>Predavač:</p>
        <input
          type="text"
          name="predavac"
          placeholder="Predavač"
          id="predavac"
          value={predavac}
          onChange={handlePredavac}
        />
      </label>

      <label>
        <p>Naziv predmeta:</p>
        <textarea
          type="text"
          name="predmet"
          placeholder="Naziv predmeta"
          id="predmet"
          value={predmet}
          onChange={handlePredmet}
        />
      </label>
      <button type="submit">Dodaj materijal</button>
    </form>
  );
};

export default DodajPredmet;
