import { React, useState } from "react";
import axios from "axios";

const DodajKategoriju = () => {
  const [moderator, setModerator] = useState("");
  const handleModerator = (event) => {
    setModerator(event.target.value);
  };

  const [kategorija, setKategorija] = useState("");
  const handleKategorija = (event) => {
    setKategorija(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const data = {
      moderator: moderator,
      naziv: kategorija,
    };

    axios
      .post("http://localhost:5000/post", data)
      .then((res) => {
        setModerator("");
        setKategorija("");
      })
      .catch((err) => {
        console.log({ message: err.message });
      });
  };
  return (
    <form onSubmit={handleSubmit}>
      <label>
        <p>Moderator:</p>
        <input
          type="text"
          name="moderator"
          placeholder="Moderator"
          id="moderator"
          value={moderator}
          onChange={handleModerator}
        />
      </label>

      <label>
        <p>Naziv kategorije:</p>
        <input
          type="text"
          name="kategorija"
          placeholder="Naziv kategorije"
          id="kategorija"
          value={kategorija}
          onChange={handleKategorija}
        />
      </label>
      <button type="submit">Dodaj kategoriju</button>
    </form>
  );
};

export default DodajKategoriju;
